mapper.select:
SELECT c.complaint_no AS complaintNo, c.title, c.category, c.status, c.created_date AS createdDate, COUNT(pl.complaint_no) AS likeCount FROM complaint c LEFT JOIN post_like pl ON c.complaint_no = pl.complaint_no
AND c.category = #{category}
AND c.status = #{status}
AND EXISTS ( SELECT 1 FROM complaint_agency ca WHERE ca.complaint_no = c.complaint_no AND ca.agency_no = #{agencyNo} )
AND ( c.title ILIKE CONCAT('%', #{search}, '%') OR c.content ILIKE CONCAT('%', #{search}, '%') )
GROUP BY c.complaint_no, c.title, c.category, c.status, c.created_date ORDER BY c.complaint_no DESC LIMIT #{limit} OFFSET #{offset}
